	function combinations(arr, n)
	{
	    var res = Array();
	    for(var i=0; i<arr[n].length ; i++){
	        var item = arr[n][i];

	        if (n == (arr.length)-1){
	            res.push(item);
	        }
	        else
	        {
	            var combs = combinations(arr,n+1);
	            for(var j=0; j<combs.length ; j++){
	                var comb = combs[j];
	                res.push(item  + comb);
	            }
	        }
	    }
	    return res;
	}


	function removewildchar(select)
	{
		if(select != null && select.length >0){
			var index = select.indexOf('*');
			if (index > -1) {
	    		select.splice(index, 1);
			}
		}else{
			select = [];
		}
		return select;
	}




